<template>
  <div class="register">
    <h1 class="title">Recuperación de contraseña</h1>
    <form action class="form" @submit.prevent="register">
      <label class="form-label" for="#email">Email:</label>
      <input
        v-model="email"
        class="form-input"
        type="email"
        id="email"
        required
        placeholder="Email"
      />
      <div class="loader">
        <img
          v-if="status"
          style="width: 15px; padding:20px; text-align: center;"
          src="data:image/gif;base64,"
        />
      </div>
      <label class="error">{{ datos.details }}</label>
      <input class="form-submit" type="submit" value="Sign Up" />
    </form>
  </div>
</template>

<script>
export default {
  data: () => ({
    email: "",
    password: "",
    passwordRepeat: "",
    status: false,
    datos: {
      code: "",
      message: "",
      details: "",
    },
  }),
  methods: {
    register() {
      let This = this;

      const requestOptions = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          plantilla: "gnp",
          usuario: this.email,
        }),
      };
      fetch(
        "https://back-dot-gnpac-317823.uc.r.appspot.com/api/v1/auth/olvido",
        requestOptions
      )
        .then((response) => response.json())
        .then(function(data) {
          if (data.code) {
            This.status = false;
            This.datos.details = data.details;
          } else {
            This.status = true;
            This.datos.details = "Se ha enviado un correo de recuperación";
          }
        });
    },
  },
};
</script>

